import {DELETE, GET_ALL, POST, POSTCOMMENT, UPDATE, VIEW} from "./types";
import EmployeeService from "../services/EmployeeService";

export const createEmployee = (employee) => async (dispatch) => {
    try {
        const res = await EmployeeService.createEmployee(employee);

        dispatch({
            type: POST,
            payload: employee,
        });
    } catch (err) {
        console.log(err);
    }
};

export const fetchPosts = () => async (dispatch) => {
    try {
        const res = await EmployeeService.getEmployees();

        dispatch({
            type: GET_ALL,
            payload: res.data,
        });
    } catch (err) {
        console.log(err);
    }
};


export const deleteEmployee = (id) => async (dispatch) => {

    try {
        await EmployeeService.deleteEmployee(id);

        dispatch({
            type: DELETE,
            payload: id
        });
    } catch (err) {
        console.log(err);
    }


}

export const updateEmployee = (id, data) => async (dispatch) => {
    try {
        // const res = await EmployeeService.updateEmployeeById(id, data);

        const res = EmployeeService.updateEmployeeById(id, data)


        dispatch({
            type: UPDATE,
            payload: id, data
        });

        return Promise.resolve(res.data);
    } catch (err) {
        return Promise.reject(err);
    }
};

export const ViewEmployee = (id) => async (dispatch) => {

    // try {
        const res = await EmployeeService.getEmployeeById(id);

        dispatch({
            type: VIEW,
            payload: res.data
        });
    // } catch (err) {
    //     console.log(err);
    // }


}

export const SaveComment=(comment)=>async (dispatch)=>{
    await EmployeeService.createComment(comment);

    dispatch({
        type: POSTCOMMENT,
        payload: comment
    });
}
