import {DELETE, GET_ALL, POST, UPDATE} from "./types";

const initialState={

    employees:[]
}
export const postsReducer = (state= initialState, action) => {
    const { type, payload } = action;

    switch (action.type){
        // case POST:
        //     return {...state,posts: state.posts.concat([action.payload])}

        case GET_ALL:
            return {...state,employees: payload}

        case DELETE:
            return state.employees.filter(({ id }) => id !== action.id);



