import React, {Component} from 'react';
import {Card} from "react-bootstrap";
import {connect} from "react-redux";
import {SaveComment} from "../redux/actions";

class CreateComment extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            status: "",
            text: "",
            author: ""
        }


        this.changeStatusHandler = this.changeStatusHandler.bind(this)
        this.changeTextHandler = this.changeTextHandler.bind(this)
        this.changeAuthorHandler = this.changeAuthorHandler.bind(this)
        this.saveComment = this.saveComment.bind(this)

        // console.log(this.props.id)
    }


    changeStatusHandler = (event) => {
        this.setState({status: event.target.value})
    };
    changeTextHandler = (event) => {
        this.setState({text: event.target.text})
    };
    changeAuthorHandler = (event) => {
        this.setState({author: event.target.value})
    };


    saveComment = (e) => {
        e.preventDefault();
        let comment = {
            id: "",
            author: this.state.name,
            status: this.state.status,
            text: this.state.descriptions

        }
        console.log(JSON.stringify(comment))


        this.props.SaveComment(this.props.id, comment);

    }


    cancel = () => {
        this.props.history.push('/employees')
    }

    getTitle() {
        // if (this.state.id === '_add') {
        return <h3 className={"text-center"}>Add Employee</h3>
        // } else {
        //     return <h3 className={"text-center"}>Update Employee</h3>
        // }
    }

    render() {

        return (
            <Card className={"col-md-6 offset-md-3"}>
                {this.getTitle()}
                <div className={"card-body"}>
                    <form>
                        <div className={"form-group"}>
                            <label>Author</label>
                            <input placeholder={"Author"} name={"author"} className={"form-control"}
                                   value={this.state.author}
                                   onChange={this.changeAuthorHandler}
                            />
                        </div>

                        <div className={"form-group"}>
                            <label>Status</label>
                            <input placeholder={"Status"} name={"status"} className={"form-control"}
                                   value={this.state.status}
                                   onChange={this.changeStatusHandler}
                            />
                        </div>

                        <div className={"form-group mb-2"}>
                            <label>Text</label>
                            <input placeholder={"Text"} name={"text"} className={"form-control"}
                                   value={this.state.text}
                                   onChange={this.changeTextHandler}
                            />
                        </div>


                        <button className={"btn btn-success"} onClick={this.saveComment}>Save
                        </button>

                    </form>
                </div>

            </Card>
        );
    }
}


const mapDispatchToProps = {
    SaveComment
}
export default connect(null, mapDispatchToProps)(CreateComment);
