import React, {Component} from 'react';
import {Container, Row} from "react-bootstrap";
import {connect} from "react-redux";
import {deleteIssue, fetchPosts, GetUser} from "../redux/actions";

class ListIssueComponent extends Component {
    constructor(props) {
        super(props);

        this.addEmployee = this.addEmployee.bind(this)

        this.viewEmployee = this.viewEmployee.bind(this)

        // this.props.GetUser();
        this.props.fetchPosts();

    }

    // componentDidMount() {
    //     EmployeeService.getEmployees().then((res) => {
    //         this.setState({employees: res.data})
    //
    //     });
    //
    // }

    addEmployee() {
        this.props.history.push('/add-employee/_add')
    }






    viewEmployee(id) {
        // this.props.ViewEmployee(id)
        this.props.history.push(`/view-employee/${id}`)
    }

    render() {

        function colorStatus(status) {
            switch (status){

                case "Created":return <b className={"text-success"}>{status}</b>
                case "Closed":return <b className={"text-warning"}>{status}</b>
                case "Closed":return <b className={"text-danger"}>{status}</b>
            }
        }

        return (
                <>


                    <h2 className={"text-center"}>Employee List</h2>
                    <Container>
                    <Row className={"border p-3}"}>
                        <button className={"btn btn-primary"} onClick={this.addEmployee}>Add employee</button>
                    </Row>


                            {
                                this.props.employees.map(
                                    employee =>
                                        <Row className={"border p-3"} key={employee.id} onClick={() => this.viewEmployee(employee.id)}>
                                            <div>{employee.name} {colorStatus(employee.status)}  </div>



                                            <div>by: {employee.author}</div>
                                            <div >#{employee.id}  Opened {employee.date}

                                                <img height={20} src={"https://image.flaticon.com/icons/png/512/3096/3096673.png"}  />
                                                     {/*//onClick={() => this.props.deleteIssue(employee.id)}/>*/}

                                                {/*<button onClick={() => this.viewEmployee(employee.id)}*/}
                                                {/*        className={"btn btn-success m-1"}>View*/}
                                                {/*</button>*/}
                                            </div>

                                        </Row>
                                )
                            }

                    </Container>

                </>
            );
        }

}

const mapDispatchToProps = {
    fetchPosts, deleteIssue,GetUser
}

const mapStateToProps = sate => ({
    employees: sate.posts.employees,
    user: sate.users.user
})

export default connect(mapStateToProps, mapDispatchToProps)(ListIssueComponent);
