import {DELETE, GET_ALL, POST, UPDATE} from "./types";


const initialState={

    employees:[

    ]
}
export const postsReducer = (state= initialState, action) => {
    const { type, payload,data } = action;

    switch (action.type){
        // case POST:
        //     return {...state,posts: state.posts.concat([action.payload])}

        case GET_ALL:
            return {...state,employees: payload}

        case DELETE:
            return {...state,employees:state.employees.filter(({ id }) => id !== payload)};


        case UPDATE :
                //return {...state,employees: state.employees.map((post))}
                return {...state,employees:state.employees.map((employee)=>{
                        if(employee.id === payload.id) {
                            employee.firstName=data.firstName
                            employee.lastName=data.lastName
                            employee.email=data.email
                            return {
                                employee
                            }
                        } else return employee;
                    })}
        default:return state
    }
};
