import React, {Component} from 'react';
import {Row} from "react-bootstrap";
import EmployeeService from "../services/EmployeeService";
import {connect} from "react-redux";
import {deleteEmployee, fetchPosts} from "../redux/actions";

class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props);

        this.addEmployee = this.addEmployee.bind(this)
        this.editEmployee = this.editEmployee.bind(this)
        this.viewEmployee  = this.viewEmployee.bind(this)

        this.props.fetchPosts();

    }

    // componentDidMount() {
    //     EmployeeService.getEmployees().then((res) => {
    //         this.setState({employees: res.data})
    //
    //     });
    //
    // }

    addEmployee() {
        this.props.history.push('/add-employee/_add')
    }


    editEmployee(id) {
        // console.log(`/employees/${id}`)
        this.props.history.push(`/add-employee/${id}`)
    }



    viewEmployee(id){
        this.props.history.push(`/view-employee/${id}`)
    }

    render() {

        return (
            <div>
                <h2 className={"text-center"}>Employee List</h2>
                <Row>
                    <button className={"btn btn-primary"} onClick={this.addEmployee}>Add employee</button>
                </Row>
                <Row>
                    <table className={"table table-striped table-bordered"}>
                        <thead>
                        <tr>
                            <th>Employee First Name</th>
                            <th>Employee Last Name</th>
                            <th>Employee Email</th>
                            <th>Actions</th>
                        </tr>
                        </thead>

                        <tbody>
                        {
                            this.props.employees.map(
                                employee =>
                                    <tr key={employee.id}>
                                        <td>{employee.firstName}</td>
                                        <td>{employee.lastName}</td>
                                        <td>{employee.email}</td>
                                        <td>
                                            <button onClick={() => this.editEmployee(employee.id)}
                                                    className={"btn btn-info m-1"}>Update
                                            </button>

                                            <button onClick={() =>this.props.deleteEmployee(employee.id)}
                                                    className={"btn btn-danger m-1"}>Delete
                                            </button>
                                            <button onClick={() => this.viewEmployee(employee.id)}
                                                    className={"btn btn-success m-1"}>View
                                            </button>
                                        </td>

                                    </tr>
                            )
                        }
                        </tbody>

                    </table>

                </Row>
            </div>
        );
    }
}

const mapDispatchToProps={
    fetchPosts,deleteEmployee
}

const  mapStateToProps= sate=>({
    employees:sate.payload.employees
})

export default connect(mapStateToProps, mapDispatchToProps) (ListEmployeeComponent);
