import React, {Component} from 'react';
import {Card} from "react-bootstrap";
import {createIssue, fetchPosts} from "../redux/actions";
import {connect} from "react-redux";
import {Field, reduxForm} from "redux-form";
import {maxLengthCreator, required} from "../validations/validatiors";

class CreateIssueComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            name: "",
            status: "",
            descriptions: "",
            author: ""
        }

    }


    cancel = () => {
        this.props.history.push('/employees')
    }


    render() {

        const onSubmit = (formData) => {
            console.log(formData)

            this.props.createIssue(formData);

            this.props.history.push('/employees')


            this.props.fetchPosts();
        }
        return (


            <Card className={"col-md-6 offset-md-3"}>
                <h3 className={"text-center"}>Add Employee</h3>
                <div className={"card-body"}>
                    <IssueReduxForm onSubmit={onSubmit}/>

                </div>
                <button className={"btn btn-danger m-2"}
                        onClick={this.cancel.bind(this)}
                >Cancel
                </button>
            </Card>


        );
    }
}

const mapDispatchToProps = {
    fetchPosts, createIssue
}


export default connect(null, mapDispatchToProps)(CreateIssueComponent);

// export const TestReduxForm = reduxForm({form:'Issue'})(CreateIssueComponent)

const Form = (props) => {
    return (<form
        onSubmit={props.handleSubmit}
    >
        <div className={"form-group"}>
            <label>Name</label>

            <Field component={"input"} placeholder={"Name"} name={"name"} className={"form-control"}
                   validate={[required,maxLengthCreator(10)]}
            />

        </div>

        <div className={"form-group"}>
            <label>Status</label>
            <Field component={"input"} placeholder={"Status"} name={"status"} className={"form-control"}

            />
        </div>

        <div className={"form-group mb-2"}>
            <label>Descriptions</label>
            <Field component={"input"} placeholder={"Descriptions"} name={"descriptions"}
                   className={"form-control"}

            />
        </div>
        <div className={"form-group mb-2"}>
            <label>Author</label>
            <Field component={"input"} placeholder={"Author"} name={"author"} className={"form-control"}

            />
        </div>

        <button className={"btn btn-success"}
            //onClick={this.saveEmployee}
        >Save
        </button>

    </form>)
}

const IssueReduxForm = reduxForm({form: 'Issue'})(Form)
