import React, {Component} from 'react';
import {Card, Container, Row} from "react-bootstrap";
import EmployeeService from "../services/EmployeeService";
import {createEmployee, fetchPosts, updateEmployee, ViewEmployee} from "../redux/actions";
import {connect} from "react-redux";

class CreateEmployeeComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            firstName: "",
            lastName: "",
            email: ""
        }




        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this)
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this)
        this.changeEmailHandler = this.changeEmailHandler.bind(this)
        this.saveOrUpdateEmployee = this.saveOrUpdateEmployee.bind(this)


    }

    componentDidMount() {
        if (this.props.match.params.id!== "_add") {

            this.props.ViewEmployee(this.props.match.params.id);

                this.setState({firstName: this.props.employee.firstName, lastName: this.props.employee.lastName, email: this.props.employee.email})
            };

    }

    changeFirstNameHandler = (event) => {
        this.setState({firstName: event.target.value})
    };
    changeLastNameHandler = (event) => {
        this.setState({lastName: event.target.value})
    };
    changeEmailHandler = (event) => {
        this.setState({email: event.target.value})
    };
    

    saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        // let employee = {firstName: this.state.firstName, lastName: this.state.lastName, email: this.state.email}
        // console.log(JSON.stringify(employee))

        if (this.props.employee.id !== "_add") {
            this.props.updateEmployee(this.props.employee.id, this.props.employee)

            this.props.history.push('/employees')

        } else {
            this.props.createEmployee(this.props.employee);
            //EmployeeService.createEmployee(employee).then(res => {
            this.props.history.push('/employees')
            //})
        }

        this.props.fetchPosts();
    }


    cancel = () => {
        this.props.history.push('/employees')
    }

    getTitle() {
        if (this.props.employee.id === '_add') {
            return <h3 className={"text-center"}>Add Employee</h3>
        } else {
            return <h3 className={"text-center"}>Update Employee</h3>
        }
    }

    render() {
        return (
            <div>
                <Container>
                    <Row>
                        <Card className={"col-md-6 offset-md-3"}>
                            {this.getTitle()}
                            <div className={"card-body"}>
                                <form>
                                    <div className={"form-group"}>
                                        <label>First Name</label>
                                        <input placeholder={"First Name"} name={"firstName"} className={"form-control"}
                                               value={this.state.firstName}
                                               onChange={this.changeFirstNameHandler}
                                        />
                                    </div>

                                    <div className={"form-group"}>
                                        <label>Last Name</label>
                                        <input placeholder={"Last Name"} name={"lastName"} className={"form-control"}
                                               value={this.state.lastName}
                                               onChange={this.changeLastNameHandler}
                                        />
                                    </div>

                                    <div className={"form-group mb-2"}>
                                        <label>Email</label>
                                        <input placeholder={"Email"} name={"email"} className={"form-control"}
                                               value={this.state.email}
                                               onChange={this.changeEmailHandler}
                                        />
                                    </div>

                                    <button className={"btn btn-success"} onClick={this.saveOrUpdateEmployee}>Save
                                    </button>
                                    <button className={"btn btn-danger"} onClick={this.cancel.bind(this)}
                                            style={{marginLeft: "10px"}}>Cancel
                                    </button>
                                </form>
                            </div>

                        </Card>
                    </Row>

                </Container>

            </div>
        );
    }
}

const mapDispatchToProps = {
    updateEmployee, fetchPosts, createEmployee, ViewEmployee
}
const mapStateToProps = sate => ({
    employee: sate.posts.employee

})


export default connect(mapStateToProps, mapDispatchToProps)(CreateEmployeeComponent);
