import React, {Component} from 'react';
import {Card, Container, Row} from "react-bootstrap";
import EmployeeService from "../services/EmployeeService";
import {createEmployee, fetchPosts, updateEmployee} from "../redux/actions";
import {connect} from "react-redux";

class CreateEmployeeComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            name: "",
            status: "",
            descriptions: "",
            author: ""
        }


        // this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this)
        // this.changeLastNameHandler = this.changeLastNameHandler.bind(this)
        // this.changeEmailHandler = this.changeEmailHandler.bind(this)


        this.changeNameHandler=this.changeNameHandler(this)
        this.changeStatusHandler=this.changeStatusHandler(this)
        this.changeDescriptionsHandler=this.changeDescriptionsHandler(this)
        this.changeAuthorHandler=this.changeAuthorHandler(this)
        this.saveOrUpdateEmployee = this.saveOrUpdateEmployee.bind(this)


    }
    componentDidMount() {
        if (this.state.id !== "_add") {
        EmployeeService.getEmployeeById(this.state.id).then((res) => {
            let employee = res.data;
            this.setState({name: employee.name, status: employee.status, descriptions: employee.descriptions,author: employee.author})
        });}
    }


    changeNameHandler = (event) => {
        this.setState({name: event.target.value})
    };
    changeStatusHandler = (event) => {
        this.setState({status: event.target.value})
    };
    changeDescriptionsHandler = (event) => {
        this.setState({descriptions: event.target.value})
    };
    changeAuthorHandler = (event) => {
        this.setState({author: event.target.value})
    };


    saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        let employee = {name: this.state.name, status: this.state.status, descriptions: this.state.descriptions,author: this.state.author}
        console.log(JSON.stringify(employee))

        if (this.state.id !== "_add") {
            this.props.updateEmployee(this.state.id, employee)

            this.props.history.push('/employees')

        } else {
            this.props.createEmployee(employee);
            //EmployeeService.createEmployee(employee).then(res => {
            this.props.history.push('/employees')
            //})
        }

        this.props.fetchPosts();
    }


    cancel = () => {
        this.props.history.push('/employees')
    }

    getTitle() {
        if (this.state.id === '_add') {
            return <h3 className={"text-center"}>Add Employee</h3>
        } else {
            return <h3 className={"text-center"}>Update Employee</h3>
        }
    }

    render() {

        return (
            <div>
                <Container>
                    <Row>
                        <Card className={"col-md-6 offset-md-3"}>
                            {this.getTitle()}
                            <div className={"card-body"}>
                                <form>
                                    <div className={"form-group"}>
                                        <label>Name</label>
                                        <input placeholder={"Name"} name={"name"} className={"form-control"}
                                               value={this.state.name}
                                               onChange={this.changeNameHandler}
                                        />
                                    </div>

                                    <div className={"form-group"}>
                                        <label>Status</label>
                                        <input placeholder={"Status"} name={"status"} className={"form-control"}
                                               value={this.state.status}
                                               onChange={this.changeStatusHandler}
                                        />
                                    </div>

                                    <div className={"form-group mb-2"}>
                                        <label>Descriptions</label>
                                        <input placeholder={"Descriptions"} name={"descriptions"} className={"form-control"}
                                               value={this.state.descriptions}
                                               onChange={this.changeDescriptionsHandler}
                                        />
                                    </div>
                                    <div className={"form-group mb-2"}>
                                        <label>Author</label>
                                        <input placeholder={"Author"} name={"author"} className={"form-control"}
                                               value={this.state.author}
                                               onChange={this.changeAuthorHandler}
                                        />
                                    </div>

                                    <button className={"btn btn-success"} onClick={this.saveOrUpdateEmployee}>Save
                                    </button>
                                    <button className={"btn btn-danger"} onClick={this.cancel.bind(this)}
                                            style={{marginLeft: "10px"}}>Cancel
                                    </button>
                                </form>
                            </div>

                        </Card>
                    </Row>

                </Container>

            </div>
        );
    }
}

const mapDispatchToProps = {
    updateEmployee, fetchPosts, createEmployee
}


export default connect(null, mapDispatchToProps)(CreateEmployeeComponent);
