import React, {Component} from 'react';
import {Row} from "react-bootstrap";
import {connect} from "react-redux";
import {deleteIssue, fetchPosts, GetUser} from "../redux/actions";

class ListIssueComponent extends Component {
    constructor(props) {
        super(props);

        this.addEmployee = this.addEmployee.bind(this)

        this.viewEmployee = this.viewEmployee.bind(this)

        // this.props.GetUser();
        this.props.fetchPosts();

    }

    // componentDidMount() {
    //     EmployeeService.getEmployees().then((res) => {
    //         this.setState({employees: res.data})
    //
    //     });
    //
    // }

    addEmployee() {
        this.props.history.push('/add-employee/_add')
    }






    viewEmployee(id) {
        // this.props.ViewEmployee(id)
        this.props.history.push(`/view-employee/${id}`)
    }

    render() {

            return (
                <div>


                    <h2 className={"text-center"}>Employee List</h2>
                    <Row>
                        <button className={"btn btn-primary"} onClick={this.addEmployee}>Add employee</button>
                    </Row>
                    <Row>
                        <table className={"table table-striped table-bordered"}>
                            <thead>
                            <tr>
                                <th>Name</th>
                                <th>Status</th>
                                <th>Descriptions</th>
                                <th>Author</th>
                                <th>Date</th>
                                <th>Actions</th>
                            </tr>
                            </thead>

                            <tbody>
                            {
                                this.props.employees.map(
                                    employee =>
                                        <tr key={employee.id}>
                                            <td>{employee.name}</td>
                                            <td>{employee.status}</td>
                                            <td>{employee.descriptions}</td>
                                            <td>{employee.author}</td>
                                            <td>{employee.date}</td>
                                            <td>


                                                <button onClick={() => this.props.deleteIssue(employee.id)}
                                                        className={"btn btn-danger m-1"}>Delete
                                                </button>
                                                <button onClick={() => this.viewEmployee(employee.id)}
                                                        className={"btn btn-success m-1"}>View
                                                </button>
                                            </td>

                                        </tr>
                                )
                            }
                            </tbody>

                        </table>

                    </Row>
                </div>
            );
        }

}

const mapDispatchToProps = {
    fetchPosts, deleteIssue,GetUser
}

const mapStateToProps = sate => ({
    employees: sate.posts.employees,
    user: sate.users.user
})

export default connect(mapStateToProps, mapDispatchToProps)(ListIssueComponent);
