import React, {Component} from 'react';
import {Row} from "react-bootstrap";
import {connect} from "react-redux";
import {deleteIssue, fetchPosts, GetUser} from "../redux/actions";

class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props);

        this.addEmployee = this.addEmployee.bind(this)
        this.editEmployee = this.editEmployee.bind(this)
        this.viewEmployee = this.viewEmployee.bind(this)

        this.props.fetchPosts();
        this.props.GetUser();

    }

    // componentDidMount() {
    //     EmployeeService.getEmployees().then((res) => {
    //         this.setState({employees: res.data})
    //
    //     });
    //
    // }

    addEmployee() {
        this.props.history.push('/add-employee/_add')
    }


    editEmployee(id) {
        // console.log(`/employees/${id}`)

        this.props.history.push(`/add-employee/${id}`)
    }


    viewEmployee(id) {
        // this.props.ViewEmployee(id)
        this.props.history.push(`/view-employee/${id}`)
    }

    render() {
        console.log(this.props.user.id)

        if(!this.props.user.id)
        {return  <a href={"https://accounts.google.com/o/oauth2/v2/auth/oauthchooseaccount?client_id=177528665108-iaph4o8tgb6lm7h6vg44m2pvicuhhv7d.apps.googleusercontent.com&redirect_uri=http%3A%2F%2Flocalhost%3A8081%2Flogin&response_type=code&scope=openid%20email%20profile&state=4SKJR2&flowName=GeneralOAuthFlow"}
                    target="_blank">Login</a>
        }
        else{
            return (
                <div>


                    <h2 className={"text-center"}>Employee List</h2>
                    <Row>
                        <button className={"btn btn-primary"} onClick={this.addEmployee}>Add employee</button>
                    </Row>
                    <Row>
                        <table className={"table table-striped table-bordered"}>
                            <thead>
                            <tr>
                                <th>Name</th>
                                <th>Status</th>
                                <th>Descriptions</th>
                                <th>Author</th>
                                <th>Date</th>
                                <th>Actions</th>
                            </tr>
                            </thead>

                            <tbody>
                            {
                                this.props.employees.map(
                                    employee =>
                                        <tr key={employee.id}>
                                            <td>{employee.name}</td>
                                            <td>{employee.status}</td>
                                            <td>{employee.descriptions}</td>
                                            <td>{employee.author}</td>
                                            <td>{employee.date}</td>
                                            <td>
                                                {/*<button onClick={() => this.editEmployee(employee.id)}*/}
                                                {/*        className={"btn btn-info m-1"}>Update*/}
                                                {/*</button>*/}

                                                <button onClick={() => this.props.deleteIssue(employee.id)}
                                                        className={"btn btn-danger m-1"}>Delete
                                                </button>
                                                <button onClick={() => this.viewEmployee(employee.id)}
                                                        className={"btn btn-success m-1"}>View
                                                </button>
                                            </td>

                                        </tr>
                                )
                            }
                            </tbody>

                        </table>

                    </Row>
                </div>
            );
        }
    }
}

const mapDispatchToProps = {
    fetchPosts, deleteIssue,GetUser
}

const mapStateToProps = sate => ({
    employees: sate.posts.employees,
    user: sate.users.user
})

export default connect(mapStateToProps, mapDispatchToProps)(ListEmployeeComponent);
