export const fetchPosts = () => async (dispatch) => {
    try {
        const res = await EmployeeService.getEmployees();
        console.log(res.data)
        dispatch({
            type: GET_ALL,
            payload: res.data,
        });
    } catch (err) {
        console.log(err);
    }
};
