import React, {Component} from 'react';
import {Card, Row} from "react-bootstrap";
import {ViewEmployee} from "../redux/actions";
import {connect} from "react-redux";

class ViewEmployeeComponent extends Component {
    constructor(props) {
        super(props);

        // this.state = {
        //      id: this.props.match.params.id,
        //     employee: {}
        // }
        //
        this.props.ViewEmployee(this.props.match.params.id);
        this.getComment=this.getComment.bind(this);
    }

    // componentDidMount() {
    //
    //     // EmployeeService.getEmployeeById(this.state.id).then((res) => {
    //     //
    //     //     this.setState({employee: res.data})
    //     // });
    //
    //
    // }
    getComment(){
        return  <tbody> {
            this.props.employee.comments.map(
                comment =>
                    <tr key={comment.id}>
                        <td>{comment.status}</td>
                        <td>{comment.author}</td>
                        <td>{comment.text}</td>

                    </tr>

            )
        }
        </tbody>
    }
    render() {

        return (
            <Card className={"col-md-6 offset-md-3"}>
                <h3>View Employee Details</h3>
                <div className={"card-body"}>
                    <Row>
                        Name is &nbsp;
                        {this.props.employee.name}
                    </Row>
                    <Row>
                        Status is &nbsp;
                        {this.props.employee.status}
                    </Row>
                    <Row>
                        Descriptions is &nbsp;
                        {this.props.employee.descriptions}
                    </Row>

                    <Row>
                        Author is &nbsp;
                        {this.props.employee.author}
                    </Row>

                    <Row>
                        Date is &nbsp;
                        {this.props.employee.date}
                    </Row>

                <table className={"table table-striped table-bordered"}>
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Status</th>
                        <th>Descriptions</th>

                    </tr>
                    </thead>



                    {this.getComment()}



                </table>
                </div>

            </Card>
        );
    }
}


const mapDispatchToProps = {
    ViewEmployee
}
const mapStateToProps = sate => ({
    employee: sate.posts.employee

})


export default connect(mapStateToProps, mapDispatchToProps)(ViewEmployeeComponent)
