import React, {Component} from 'react';
import {Card, Row} from "react-bootstrap";
import {ViewEmployee} from "../redux/actions";
import {connect} from "react-redux";

class ViewEmployeeComponent extends Component {
    constructor(props) {
        super(props);

        // this.state = {
        //      id: this.props.match.params.id,
        //     employee: {}
        // }
         this.props.ViewEmployee(this.props.match.params.id);
    }

    // componentDidMount() {
    //
    //     // EmployeeService.getEmployeeById(this.state.id).then((res) => {
    //     //
    //     //     this.setState({employee: res.data})
    //     // });
    //
    //
    // }

    render() {

        return (
            <Card className={"col-md-6 offset-md-3"}>
                <h3>View Employee Details</h3>
                <div className={"card-body"}>
                    <Row>
                         Name is &nbsp;
                        { this.props.employee.name}
                    </Row>
                    <Row>
                        Status is &nbsp;
                        { this.props.employee.status}
                    </Row>
                    <Row>
                        Descriptions is &nbsp;
                        { this.props.employee.descriptions}
                    </Row>

                    <Row>
                        Author is &nbsp;
                        { this.props.employee.author}
                    </Row>

                    <Row>
                        Date is &nbsp;
                        { this.props.employee.date}
                    </Row>

                    {
                        this.props.employee.map(
                            employee =>
                                <tr key={employee.id}>
                                    <td>{employee.name}</td>
                                    <td>{employee.status}</td>
                                    <td>{employee.descriptions}</td>
                                    <td>{employee.author}</td>
                                    <td>{employee.date}</td>
                                    <td>
                                        {/*<button onClick={() => this.editEmployee(employee.id)}*/}
                                        {/*        className={"btn btn-info m-1"}>Update*/}
                                        {/*</button>*/}

                                        <button onClick={() =>this.props.deleteEmployee(employee.id)}
                                                className={"btn btn-danger m-1"}>Delete
                                        </button>
                                        <button onClick={() => this.viewEmployee(employee.id)}
                                                className={"btn btn-success m-1"}>View
                                        </button>
                                    </td>

                                </tr>
                        )
                    }
                </div>

            </Card>
        );
    }
}


const mapDispatchToProps = {
    ViewEmployee
}
const mapStateToProps = sate => ({
    employee: sate.posts.employee

})



export default connect(mapStateToProps, mapDispatchToProps)(ViewEmployeeComponent)
