//
//    return  axios.get(EMPLOYEE_API_BASE_URL).then((res) => {
//             this.setState({employees: res.data})
//
//         });
// }

const _getBooks = (books) => ({
    type: 'GET_s',
    employees
});

export const getBooks = () => {
    return (dispatch) => {
        return axios.get(EMPLOYEE_API_BASE_URL).then(result => {
            const books = [];

            result.data.forEach(item => {
                books.push(item);
            });

            dispatch(_getBooks(books));
        });
    };
};
