import {GET_ALL, POST} from "./types";

const initialState={
    // posts:[],
    // fetchedPosts:[]
    employees:[]
}
export const postsReducer = (state= initialState, action) => {

    switch (action.type){
        // case POST:
        //     return {...state,posts: state.posts.concat([action.payload])}

        case GET_ALL:
            return {...state,fetchedPosts: action.payload}
        default:return state
    }
};
