import React, {Component} from 'react';
import {Card, Row} from "react-bootstrap";
import {ViewEmployee} from "../redux/actions";
import {connect} from "react-redux";

class ViewEmployeeComponent extends Component {
    constructor(props) {
        super(props);

            this.state = {
                id: "",
                status: "",
                author: "",
                text: ""
            }

        this.props.ViewEmployee(1);
    }

    // componentDidMount() {
    //
    //     // EmployeeService.getEmployeeById(this.state.id).then((res) => {
    //     //
    //     //     this.setState({employee: res.data})
    //     // });
    //
    //
    // }

    render() {

        return (
           <>
            <Card className={"col-md-6 offset-md-3"}>
                <h3>View Employee Details</h3>
                <div className={"card-body"}>
                    <Row>
                        Name is &nbsp;
                        {this.props.employee.name}
                    </Row>
                    <Row>
                        Status is &nbsp;
                        {this.props.employee.status}
                    </Row>
                    <Row>
                        Descriptions is &nbsp;
                        {this.props.employee.descriptions}
                    </Row>

                    <Row>
                        Author is &nbsp;
                        {this.props.employee.author}
                    </Row>

                    <Row>
                        Date is &nbsp;
                        {this.props.employee.date}
                    </Row>

                    <table className={"table table-striped table-bordered"}>
                        <thead>
                        <tr>
                            <th>Name</th>
                            <th>Status</th>
                            <th>Descriptions</th>

                        </tr>
                        </thead>

                        <tbody>

                        {

                            this.props.comments.map(
                                comment =>

                                    <tr key={comment.id}>
                                        <td>{comment.status}</td>
                                        <td>{comment.author}</td>
                                        <td>{comment.text}</td>

                                    </tr>
                            )
                        }

                        </tbody>

                    </table>
                </div>

            </Card>

               <Card className={"col-md-6 offset-md-3"}>
                   {this.getTitle()}
                   <div className={"card-body"}>
                       <form>
                           <div className={"form-group"}>
                               <label>Name</label>
                               <input placeholder={"Name"} name={"name"} className={"form-control"}
                                      value={this.state.name}
                                      onChange={this.changeNameHandler}
                               />
                           </div>

                           <div className={"form-group"}>
                               <label>Status</label>
                               <input placeholder={"Status"} name={"status"} className={"form-control"}
                                      value={this.state.status}
                                      onChange={this.changeStatusHandler}
                               />
                           </div>

                           <div className={"form-group mb-2"}>
                               <label>Descriptions</label>
                               <input placeholder={"Descriptions"} name={"descriptions"} className={"form-control"}
                                      value={this.state.descriptions}
                                      onChange={this.changeDescriptionsHandler}
                               />
                           </div>
                           <div className={"form-group mb-2"}>
                               <label>Author</label>
                               <input placeholder={"Author"} name={"author"} className={"form-control"}
                                      value={this.state.author}
                                      onChange={this.changeAuthorHandler}
                               />
                           </div>

                           <button className={"btn btn-success"} onClick={this.saveOrUpdateEmployee}>Save
                           </button>
                           <button className={"btn btn-danger"} onClick={this.cancel.bind(this)}
                                   style={{marginLeft: "10px"}}>Cancel
                           </button>
                       </form>
                   </div>

               </Card>

           </>
        );
    }
}


const mapDispatchToProps = {
    ViewEmployee
}
const mapStateToProps = sate => ({
    employee: sate.posts.employee,
     comments: sate.posts.comments

})


export default connect(mapStateToProps, mapDispatchToProps)(ViewEmployeeComponent)
