import {DELETE, GET_ALL, POST, POSTCOMMENT, UPDATE, VIEW} from "./types";
import axios from "axios";

const EMPLOYEE_API_BASE_URL = "http://localhost:8081/api/v1/employees"


export const createEmployee = (employee) => async (dispatch) => {
    try {
        const res = await axios.post(EMPLOYEE_API_BASE_URL, employee);

        dispatch({
            type: POST,
            payload: employee,
        });
    } catch (err) {
        console.log(err);
    }
};

export const fetchPosts = () => async (dispatch) => {
    try {
        const res = await axios.get(EMPLOYEE_API_BASE_URL);
        ;

        dispatch({
            type: GET_ALL,
            payload: res.data,
        });
    } catch (err) {
        console.log(err);
    }
};


export const deleteEmployee = (id) => async (dispatch) => {

    try {
        await axios.delete(EMPLOYEE_API_BASE_URL + "/" + id);

        dispatch({
            type: DELETE,
            payload: id
        });
    } catch (err) {
        console.log(err);
    }


}

export const updateEmployee = (id, data) => async (dispatch) => {
    try {
        // const res = await EmployeeService.updateEmployeeById(id, data);

        const res = axios.put(EMPLOYEE_API_BASE_URL + "/" + id, data)


        dispatch({
            type: UPDATE,
            payload: id, data
        });

        return Promise.resolve(res.data);
    } catch (err) {
        return Promise.reject(err);
    }
};

export const ViewEmployee = (id) => async (dispatch) => {

    // try {
    const res = await axios.get(EMPLOYEE_API_BASE_URL + "/" + id);

    dispatch({
        type: VIEW,
        payload: res.data
    });
    // } catch (err) {
    //     console.log(err);
    // }


}

export const SaveComment = (id, comment) => async (dispatch) => {
    await axios.post(EMPLOYEE_API_BASE_URL + "/" + id, comment);

    dispatch({
        type: POSTCOMMENT,
        payload: comment
    });
}
