import React, {Component} from 'react';
import {Card, Container, Row} from "react-bootstrap";
import {createIssue, fetchPosts} from "../redux/actions";
import {connect} from "react-redux";

class CreateIssueComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            name: "",
            status: "",
            descriptions: "",
            author: ""
        }


        this.changeNameHandler = this.changeNameHandler.bind(this)
        this.changeStatusHandler = this.changeStatusHandler.bind(this)
        this.changeDescriptionsHandler = this.changeDescriptionsHandler.bind(this)
        this.changeAuthorHandler = this.changeAuthorHandler.bind(this)
        this.saveEmployee = this.saveEmployee.bind(this)
        // eslint-disable-next-line react-hooks/rules-of-hooks

    }


    changeNameHandler = (event) => {

        this.setState({name: event.target.value})

    };
    changeStatusHandler = (event) => {
        this.setState({status: event.target.value})
    };
    changeDescriptionsHandler = (event) => {
        this.setState({descriptions: event.target.value})
    };
    changeAuthorHandler = (event) => {
        this.setState({author: event.target.value})
    };


    saveEmployee = (e) => {
        e.preventDefault();




        let employee = {
            name: this.state.name,
            status: this.state.status,
            descriptions: this.state.descriptions,
            author: this.state.author
        }
        // console.log(JSON.stringify(employee))


        this.props.createIssue(employee);

        this.props.history.push('/employees')


        this.props.fetchPosts();
    }


    cancel = () => {
        this.props.history.push('/employees')
    }

    getTitle() {

        return <h3 className={"text-center"}>Add Employee</h3>

    }

    render() {


        return (
            <div>
                <>

                        <Card className={"col-md-6 offset-md-3"}>
                            {this.getTitle()}
                            <div className={"card-body"}>
                                <form>
                                    <div className={"form-group"}>
                                        <label>Name</label>

                                        <input placeholder={"Name"} name={"name"} className={"form-control"}
                                               value={this.state.name}
                                               onChange={this.changeNameHandler}/>

                                    </div>

                                    <div className={"form-group"}>
                                        <label>Status</label>
                                        <input placeholder={"Status"} name={"status"} className={"form-control"}
                                               value={this.state.status}
                                               onChange={this.changeStatusHandler}/>
                                    </div>

                                    <div className={"form-group mb-2"}>
                                        <label>Descriptions</label>
                                        <input placeholder={"Descriptions"} name={"descriptions"}
                                               className={"form-control"}
                                               value={this.state.descriptions}
                                               onChange={this.changeDescriptionsHandler}/>
                                    </div>
                                    <div className={"form-group mb-2"}>
                                        <label>Author</label>

                                        <input placeholder={"Author"} name={"author"} className={"form-control"}
                                               value={this.state.author}
                                               onChange={this.changeAuthorHandler}/>
                                    </div>

                                    <button className={"btn btn-success"} onClick={this.saveEmployee}>Save
                                    </button>
                                    <button className={"btn btn-danger"} onClick={this.cancel.bind(this)}
                                            style={{marginLeft: "10px"}}>Cancel
                                    </button>
                                </form>
                            </div>

                        </Card>


                </>

            </div>
        );
    }
}

const mapDispatchToProps = {
    fetchPosts, createIssue
}


export default connect(null, mapDispatchToProps)(CreateIssueComponent);
